from PyQt5.QtGui import QPixmap, QImage, QIcon
from PyQt5.QtCore import QByteArray

class imageCollectionBase64():
	def __init__(self):
		self.appIconBase64 = b''
		self.rulerIconBase64 = b''

	def qIconFromBase64(self, base64):
		pixmap = QPixmap()
		pixmap.loadFromData(QByteArray.fromBase64(base64))
		_icon = QIcon(pixmap)
		return _icon

	def qImageFromBase64(self, base64):
		_image = QImage()
		_image.loadFromData(QByteArray.fromBase64(base64))
		return _image